import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Card from 'react-bootstrap/Card';
import { Button } from 'react-bootstrap';
const Film = () => {
const [movie,setMovie]=useState([])
  useEffect(()=>{
    axios.get("https://my-json-server.typicode.com/horizon-code-academy/fake-movies-api/movies").then((response)=>{
setMovie(response.data)
    })
  })
  return (
    <div className='container'>
      <div className='row'>

      </div>
      {movie.map((movies)=>{
        return(
          <Card style={{ width: '18rem',border:"none" }}>
          <Card.Img variant="top" src="holder.js/100px180" />
          <Card.Body>
            <Card.Title>{movies.title}</Card.Title>
            <Card.Title>{movies.Year}</Card.Title>
            <Card.Text>
            {movies.Runtime}
            </Card.Text>
            <img src={movies.Poster} style={{width:"100%"}} alt=""></img>
            <Button variant="primary">Bilet Al</Button>
          </Card.Body>
        </Card>
        )
      })}

    </div>
  )
}

export default Film
