import { useState,useEffect } from "react";
import axios from "axios"
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table';
const Api = () => {
    const [data,setData]=useState([]);
    const [status,setStatus]=useState(false);
    useEffect(() => {
        axios
          .get("https://jsonplaceholder.typicode.com/posts")
          .then((response) => {
            setData(response.data);
          })
          .catch((error) => {
            console.log(error.message);
          });
      }, []);

      return (
        <div>
             <Table striped bordered hover>
      <thead>
        <tr>
          <th>#</th>
          <th>First Name</th>
          <th>Last Name</th>
          <th>Username</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>1</td>
          <td>Mark</td>
          <td>Otto</td>
          <td>@mdo</td>
        </tr>
        <tr>
          <td>2</td>
          <td>Jacob</td>
          <td>Thornton</td>
          <td>@fat</td>
        </tr>
        <tr>
          <td>3</td>
          <td colSpan={2}>Larry the Bird</td>
          <td>@twitter</td>
        </tr>
      </tbody>
    </Table>
            {status ?
            <>

            {data.map((a) => {
            return <h6>{a.title}</h6>
          })} </> :  <Button variant="link" onClick={()=>setStatus(true)}>APİ GETİR</Button>}


        </div>
      );

}

export default Api
